import axios from 'axios'

const CarDetail = () =>{
    const [car, setCar] =useState('')
    const {id} = useParams();
    const getSingleCar = async () => {
        const{data}= await axios.get(`http://127.0.0.1:8000/${id}`)
        console.log(data)
        setCar(data)
    }
    useEffect(() =>{
        getSingleCar();
    }
,[])
    return(
        <div>
            <h1 > car detail
            </h1>
            <div> {car.model}
        </div>
        <div class="card" style={{ float: 'left',padding: '35px', width: '600px' }}>
                            <img class="card-img-top" src={`http://127.0.0.1:8000${car.image}`} alt="Card image cap" />
