                                 <h2 class="card-text">{car.model}</h2>
                                    <h4> {car.serie}</h4>
                                    <p className="card-text ">Year: {car.year}</p>
                                    <p className="card-text">Date creation Pub: {car.date}</p>
                                    <p className="card-text">Dscription: {car.description}</p>


                            </div>
                        </div>


                    </div>


    )
}
export default CarDetail;
